Router.configure({
    layoutTemplate: 'app'
});

Router.route('/chatroom', function () {
    this.redirect('/chatroom/global');
    this.render('/global');
    this.layout('app');
});

Router.route('chatroom/:channel', function () {
    this.render('messages');
});
