Router.configure({
    layoutTemplate: 'app'
});

Router.route('/chatroom/global', function () {
    this.redirect('/chatroom/global');
});

Router.route('/chatroom/members', function () {
    this.redirect('/chatroom/members');
});

Router.route('chatroom/:channel', function () {
    this.render('messages');
});

Router.route('/rules');
