Router.configure({
    layoutTemplate: 'app'
});

Router.route('/', function () {
    this.redirect('/global');
});

Router.route('/:channel', function () {
    Session.set('channel', this.params.channel);
    $(".intro").html(this.params.channel);
    this.render('messages');
});

Router.route('/:members', function () {
    return {
        as: 'members',
        to: function() {
            if (Meteor.user() && Meteor.user().username === 'admin') {
                return 'admin';
            } else {
                return 'unauthorized';
            }
        }
    };
});
