if(Meteor.isServer){
    Accounts.onCreateUser(function(options, user){
        if(options.roles){
            Roles.setUserRoles( Meteor.userId(), ['member']);
        }
        if (options.profile)
            user.profile = options.profile;
        return user;
    });
}
