Router.configure({
    layoutTemplate: 'app'
});

Router.route('/chatroom', function () {
    this.redirect('/chatroom/members');
});

Router.route('chatroom/:channel', function () {
    this.render('messages');
});

Router.route('/rules');

Meteor.Router.add({
    '/chatroom': 'chatroom',
    'add': function () { return 'test'},
    '/rooms/:id': function(id) {            //THIS IS THE ROUTER CALL TO THE ROOMS LINKS
        Session.set('currentRoomId', id);
        return 'chatRoom'
    }
});
