Router.configure({
    layoutTemplate: 'app'
});

Router.route('/', function () {
    this.redirect('/global');
});

Router.route('/:channel', function () {
    this.render('messages');
});

Router.map(function () {
    this.route('members', {
        path: 'members',
        onBeforeAction: function (pause) {
            if (!Meteor.user()) {
                Session.set('channel', 'global');
            }
            else {
                Session.set('channel', 'members');
            }
        }
    })
});
